def space_thermal_simulation(I, T_base=20, Q_rad=1e-3, conductor_length=100, tape_width=4e-3):
    # Radiation cooling: Q = 4*sigma*A*T_base^3*delta_T
    A_rad = conductor_length * tape_width
    delta_T = Q_rad / (4 * 5.67e-8 * A_rad * T_base**3)
    return {'T_final': T_base + delta_T, 'thermal_margin_K': 90 - (T_base + delta_T)}

